void generate_interrupt()
{
   uint32_t *pSTIR=(uint32_t *)0xE000EF00;
   uint32_t *pISER0=(uint32_t *)0xE000E100;
   /* 1. These are some are Cortex M4 Processor system control register which can only be accessed in priviliged access level.
    * 2. Any attempt to change the contents of these register from being in unpreviliged acces level will cause the processor fault exception
    */

//Enable IRQ3
   *pISER0 |=(1<<3);

//generate an interrupt from software for IRQ3
   *pSTIR=(3&0x1FF);
}
void AccessSwitch_1()
{
	__asm volatile("MRS R0,CONTROL");
	__asm volatile("ORR R0,R0,#0x01");
	__asm volatile("MSR CONTROL,R0");


}
void AccessSwitch_0()
{
	__asm volatile("MRS R0,CONTROL");
	__asm volatile("ORR R0,R0,#1");
	__asm volatile("MSR CONTROL,R0");
	__asm volatile("CPS #0x10");
	//__asm volatile("AND R0,R0,#0xFFFFFFFE");



}
