int main(void)
{
	printf("I,m in thread mode : Before interrupts\n");
	AccessSwitch_1();
	AccessSwitch_0();
	generate_interrupt();
	printf("I,m in thread mode : After interrupts\n");
    /* Loop forever */
	for(;;);
}


